import React, { Component } from 'react';


import './App.css'; 

// const validator = require('validator')

//import Emailchecker from '../components/emailcheck'

class App extends Component {

  constructor(props)
  {
    console.log('App.js constructor')
    super(props);
    this.state ={

      ans:[],

    }


    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleSubmit(event) {
    event.preventDefault()
    const user = event.target.elements.checkmail.value
    document.getElementById('p-1').innerHTML='<h2>Loading....</h2>';

    fetch('http://localhost:3000/user_2?Emailaddress='+user).then((response)=>{
    response.json().then((data)=>{
        console.log(data)
        if (data.error)
        {
          console.log(data.error)
          document.getElementById('p-1').innerHTML="<h1>"+data.error+"</h1>"
        }
        else if (data.info)
        {
          console.log(data)
          document.getElementById('p-1').innerHTML="<h1>"+data.info+"</h1>"
        }
        else
        {
          document.getElementById('p-1').innerHTML="<h1>ERROR</h1><h3>code: "+data.code+"</h3><h3>"+data.syscall+"</h3><h3>Host name : "+data.hostname+"</h3>"
        }
        // this.setState({ans:data})
    }).catch((error)=>{
      document.getElementById('p-1').innerHTML="<h1>error</h1>"
    })
  })


  }





  render(){
  return (
    <div className='App'>
      <div>
        <h1>Enter Your Emailaddress</h1>
      </div>
      <form id="p-2" onSubmit={this.handleSubmit}>
        <input className="input1" type="email" name="checkmail" />
        <button className="button1">Submit</button>
      </form>
      <div id="p-1">

      </div>

   </div>   
  )

  }
}

export default App;
