import React, { Component } from 'react';

import axios from 'axios'

import './App.css'; 

// const validator = require('validator')

//import Emailchecker from '../components/emailcheck'

class App extends Component {

  constructor(props)
  {
    console.log('App.js constructor')
    super(props);
    this.state ={

      ans:'',

    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(event) {    
    this.setState({ans: event.target.value});  
  };

  handleSubmit(event) {
    event.preventDefault()
    const user = event.target.elements.checkmail.value
    document.getElementById('p-1').innerHTML='<h1>Checking....</h2>';
    axios.get('http://localhost:3000/user_2?Emailaddress='+user)
    .then((response)=>{
      this.setState({ans: response});
      console.log(response.data)
    }).catch((error)=>{
      console.log(error)
    })


  }





  render(){
  return (
    <div className='App'>
      <form onSubmit={this.handleSubmit}>
        <input type="email" name="checkmail" />
        <button>Submit</button>
      </form>
      <div id="p-1">

      </div>

   </div>   
  )

  }
}

export default App;
