import React, { Component } from 'react';

import axios from 'axios'

import './App.css'; 

// const validator = require('validator')

//import Emailchecker from '../components/emailcheck'

class App extends Component {

  constructor(props)
  {
    console.log('App.js constructor')
    super(props);
    this.state ={

      ans:'',

    }

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(event) {    
    this.setState({ans: event.target.value});  
  };

  handleSubmit(event) {
    event.preventDefault()
    const user = event.target.elements.checkmail.value
    document.getElementById('p-1').innerHTML='<h1>Checking....</h2>';
    fetch("http://localhost:3000/user_2?Emailaddress=" + user, {
            "method": "GET",
            "headers": {
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Methods': 'GET, POST, PUT, DELETE, OPTIONS',
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(res => res.json()).then(data => {
                console.log(data);
                // if(data.deliverable){
                //     document.getElementById('app').innerHTML = `<h1>Valid Email : ${mailid}</h2>`;
                // }
                // else{
                //     document.getElementById('app').innerHTML = `<h1>Invalid Email : ${mailid}</h2>`;
                // }
            })
            .catch((error) => {
                //on error
                console.log(error);
            });


  }





  render(){
  return (
    <div className='App'>
      <form onSubmit={this.handleSubmit}>
        <input type="email" name="checkmail" />
        <button>Submit</button>
      </form>
      <div id="p-1">

      </div>

   </div>   
  )

  }
}

export default App;
