import React from 'react';

const emailExistence = require('email-existence')

const emailchecker =(args)=>{

    emailExistence.check('amityv0113@gmail.com', function(error, response){
        console.log('res: '+response);
        if (error){
            return <p>Error</p>

        }
        else
        {
            if (response)
            {
                return <p>Active</p>
            }
            else
            {
                return <p>Inactive</p>
            }
        }

    });


}

export default emailchecker;
